  // Increment the view counter when the page loads
  useEffect(() => {
    // Simulate an API call to fetch and update views
    fetch('/api/views', { method: 'POST' }) // This would increment the view count on the server
      .then((response) => response.json())
      .then((data) => setViews(data.views)); // Assume the server returns the updated view count
  }, []);

  // Handle the like button click
  const handleLike = () => {
    setLikes(likes + 1);
    // Optionally, you can send this like count to the server
    // fetch('/api/likes', { method: 'POST', body: JSON.stringify({ likes: likes + 1 }) });
  };

  return (
    <div className="max-w-3xl mx-auto p-6">
